mapper.select:
select

,

from product


    and product_name = #{productName,jdbcType=VARCHAR}


    and product_type = #{productType,jdbcType=VARCHAR}


    and brand = #{brand,jdbcType=VARCHAR}


    and business = #{business,jdbcType=VARCHAR}


    and present_price <= #{maxPrice,jdbcType=VARCHAR}


    and present_price >= #{minPrice,jdbcType=VARCHAR}


    and  product_status = #{productStatus,jdbcType=VARCHAR}
